import { Component } from '@angular/core';



interface Message {
  content: string;
  sentByUser: boolean;
}

interface Chat {
  id: number;
  name: string;
  messages: Message[];
}

@Component({
  selector: 'app-chatbot',
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.css']
})
export class ChatbotComponent {

}
